    def order_photos_for_mosaic(photos, rows, cols)
        page = Magick::Rectangle.new(0,0,0,0)
        photos.scene = 0

        r = rows - 1
        c = cols -1

        (0..r).each do |row_number|
          (0..c).each do |col_number|
            puts "row number is #{row_number}, col number is #{col_number}"

            page.x = col_number * 13
            page.y = row_number * 13
            puts "page is #{page}"
            puts "current photo is #{photos.cur_image.inspect}"
            photos.page = page
            puts "photos.page is now #{photos.page}"
            puts "current_image.page is now #{photos.cur_image.page}"

            photos.scene += 1 unless photos.scene == (c * r)
            puts "photo scene has been updated to #{photos.scene}"
          end
        end
        return photos
    end


    def create_mosaic(image_list)
      mosaic = image_list.mosaic
      mosaic.write("mosiac.gif")
    end

    
    def create_conversion_hash(photos) #hash of photo links to photo objects
      conversion_hash = {}
      l = Magick::ImageList.new
      i = 0
      photos.uniq.each do |p|
          begin 
            puts "opening picture in ruby"
            urlimage = open(p)
            puts "reading image into imagemagick"
            l.from_blob(urlimage.read)
            puts "done with image #{p}"
            conversion_hash[p] = l[i]
            i += 1
          rescue

          end
      end
      @conversion_hash = conversion_hash
      return conversion_hash
    end

    def convert_link_array_to_objects(link_array) #returns an array of photos objects
      l = Magick::ImageList.new
      link_array.each do |p|
        l << self.conversion_hash[p].copy.resize_to_fill!(13,13)
      end
      return l
    end

